import { Component, EventEmitter, OnChanges, OnInit, Output, SimpleChanges } from '@angular/core';
import { AuthenticateServiceService } from '../services/authenticate-service.service';
import { RouterServiceService } from '../services/router-service.service';

@Component({
  selector: 'app-navbar',
  templateUrl: './navbar.component.html',
  styleUrls: ['./navbar.component.css']
})
export class NavbarComponent implements OnInit,OnChanges {
  status:boolean;
  constructor(private routerService: RouterServiceService, private authServ:AuthenticateServiceService) { }


  ngOnInit(): void {
    this.status= this.authServ.ifAuth();
    console.log(this.status);
  }

  ngOnChanges(changes: SimpleChanges): void {
    console.log(changes);
    // this.status=this.authServ.ifAuth();
  }
  enteredSearchValue: string = ''
  // status=this.authServ.ifAuth();
@Output()
searchedValEvent: EventEmitter<string> = new EventEmitter<string>();

onSearchTextChanged()
{
  this.searchedValEvent.emit(this.enteredSearchValue)
}

logout(){
  alert("You have successfully logout.")
  // this.routerService.tologin();
  this.authServ.login();
  this.status=this.authServ.ifAuth();
  console.log(this.status);
  window.location.reload();
}
}
